// OrderInfoPanel
function OrderInfoPanel({
  onEditItem,
  isMapsLoaded,
  orderType,
  setOrderType,
  orderAddress,
  setOrderAddress,
  orderDeliveryFee,
  setOrderDeliveryFee,
  orderAddressError,
  setOrderAddressError,
  estimatedTime,
  storeOpenNow,
  preorderPickupLabel,
  onProceed,
}) {
  const { cart, totalPrice } = useCart();
  const [voucherCode, setVoucherCode] = React.useState("");
  const addressInputRef = useRef(null);
  const finalTotal = totalPrice + (orderDeliveryFee || 0);
  const isPreorder = orderType === "Pickup" && !storeOpenNow;

  useEffect(() => {
    const hasWindow = typeof window !== "undefined";
    const hasMaps = hasWindow && !!window.google?.maps;
    const hasPlacesImport =
      hasMaps && typeof window.google.maps.importLibrary === "function";

    if (
      orderType !== "Delivery" ||
      !isMapsLoaded ||
      !addressInputRef.current ||
      !hasPlacesImport
    ) {
      return;
    }

    let disposed = false;
    let widget = null;

    (async () => {
      try {
        const { PlaceAutocompleteElement } =
          await window.google.maps.importLibrary("places");

        if (disposed || !addressInputRef.current) return;

        const boundsLiteral = {
          south: -35.15,
          north: -35.0,
          west: 138.45,
          east: 138.65,
        };

        const el = new PlaceAutocompleteElement({
          componentRestrictions: { country: "AU" },
          types: ["address"],
          locationRestriction: boundsLiteral,
        });

        el.style.display = "block";
        el.style.width = "100%";

        const container = addressInputRef.current;
        container.innerHTML = "";
        container.appendChild(el);
        widget = el;

        el.addEventListener("gmp-select", async (event) => {
          const prediction = event?.placePrediction;
          if (!prediction) return;

          const place = prediction.toPlace ? prediction.toPlace() : null;
          if (!place) return;

          await place.fetchFields?.({
            fields: ["formattedAddress", "addressComponents"],
          });

          const formatted = getFormattedAddressFromPlace(place);
          const components = getAddressComponentsFromPlace(place);

          if (!components || !components.length) {
            setOrderAddressError("Please select a valid address from the suggestions.");
            setOrderDeliveryFee(0);
            return;
          }

          const suburbComponent = components.find((c) =>
            (c.types || []).includes("locality"),
          );

          if (suburbComponent) {
            const zones = {
              "sheidow park": 8.4,
              woodcroft: 8.4,
              "christie downs": 12.6,
              "trott park": 8.4,
              "happy valley": 8.4,
              "o'halloran hill": 8.4,
              "hallett cove": 12.6,
              "hackham west": 12.6,
              "huntfield heights": 12.6,
              "morphett vale": 8.4,
              lonsdale: 12.6,
              "old reynella": 8.4,
              hackham: 12.6,
              reynella: 8.4,
              "onkaparinga hills": 12.6,
              "reynella east": 8.4,
              "aberfoyle park": 12.6,
            };

            const suburbName =
              getLongNameFromComponent(suburbComponent).toLowerCase();

            setOrderAddress(formatted);

            if (zones[suburbName]) {
              setOrderDeliveryFee(zones[suburbName]);
              setOrderAddressError("");
            } else {
              setOrderDeliveryFee(0);
              setOrderAddressError("Sorry, we do not deliver to this suburb.");
            }
          } else {
            setOrderAddressError(
              "Could not determine suburb. Please try a different address.",
            );
            setOrderDeliveryFee(0);
          }
        });
      } catch (err) {
        console.error("[delivery-autocomplete] PlaceAutocompleteElement error", err);
      }
    })();

    return () => {
      disposed = true;
      try {
        if (widget && widget.parentNode) {
          widget.parentNode.removeChild(widget);
        }
      } catch (_) {}
    };
  }, [
    isMapsLoaded,
    orderType,
    setOrderAddress,
    setOrderAddressError,
    setOrderDeliveryFee,
  ]);

  const activeBtn = {
    backgroundColor: "var(--brand-neon-green)",
    color: "var(--background-dark)",
    border: "none",
    padding: "0.75rem 1rem",
    borderRadius: "0.5rem",
    fontWeight: 700,
    cursor: "pointer",
    width: "50%",
  };
  const inactiveBtn = {
    ...activeBtn,
    backgroundColor: "var(--border-color)",
    color: "var(--text-light)",
  };

  return (
    <>
      <h2 className="panel-title">Your Order</h2>

      <div style={{ display: "flex", gap: "0.5rem", marginBottom: "1rem" }}>
        <button
          style={orderType === "Pickup" ? activeBtn : inactiveBtn}
          onClick={() => {
            setOrderType("Pickup");
            setOrderAddress("");
            setOrderDeliveryFee(0);
            setOrderAddressError("");
          }}
        >
          Pickup
        </button>
        <button
          style={orderType === "Delivery" ? activeBtn : inactiveBtn}
          onClick={() => setOrderType("Delivery")}
        >
          Delivery
        </button>
      </div>

      <div className="info-box">
        {orderType === "Pickup" ? (
          <>
            <p>
              Pickup from: <strong>Pizza Peppers Store</strong>
            </p>
            <p>
              Pickup time:{" "}
              <strong>
                {storeOpenNow
                  ? `ASAP (Approx. ${estimatedTime} mins)`
                  : `Pre-order (Ready ${
                      preorderPickupLabel || "15 min after opening"
                    })`}
              </strong>
            </p>
            {!storeOpenNow && (
              <p
                style={{
                  marginTop: "0.35rem",
                  color: "var(--text-medium)",
                  fontSize: "0.85rem",
                }}
              >
                Store is closed — pickup defaults to pre-order (Adelaide time).
              </p>
            )}
          </>
        ) : (
          <>
            <label
              htmlFor="address"
              style={{
                fontWeight: 500,
                marginBottom: "0.5rem",
                display: "block",
              }}
            >
              Delivery Address
            </label>

            {isMapsLoaded ? (
              <div id="address" ref={addressInputRef} style={{ width: "100%" }} />
            ) : (
              <input
                type="text"
                id="address"
                onChange={(e) => {
                  setOrderAddress(e.target.value);
                  setOrderDeliveryFee(0);
                  setOrderAddressError("");
                }}
                value={orderAddress}
                placeholder="Enter your delivery address"
                style={{ width: "calc(100% - 1.5rem)" }}
              />
            )}

            {orderAddressError && (
              <p
                style={{
                  color: "#fca5a5",
                  marginTop: "0.5rem",
                  fontSize: "0.875rem",
                }}
              >
                {orderAddressError}
              </p>
            )}

            {estimatedTime > 0 && orderDeliveryFee > 0 && (
              <p style={{ marginTop: "1rem" }}>
                Estimated delivery time:{" "}
                <strong>ASAP (Approx. {estimatedTime} mins)</strong>
              </p>
            )}
          </>
        )}
      </div>

      <div className="cart-items-list">
        {cart.length > 0 ? (
          cart.map((it, idx) => (
            <div
              key={idx}
              className="cart-item"
              onClick={() => onEditItem(it, idx)}
            >
              <div>
                <span>
                  {it.qty} x {it.name} {formatSizeSuffix(it.size)}
                </span>
                {it.isGlutenFree && (
                  <span
                    style={{
                      color: "#facc15",
                      marginLeft: "0.5rem",
                      fontSize: "0.75rem",
                    }}
                  >
                    GF
                  </span>
                )}
                {(() => {
                  const extrasSummary = Array.isArray(it.add_ons)
                    ? it.add_ons
                        .map((opt) => opt?.name || opt?.ref)
                        .filter(Boolean)
                        .join(", ")
                    : "";
                  return extrasSummary ? (
                    <div className="cart-item-details">{extrasSummary}</div>
                  ) : null;
                })()}
                <div className="cart-item-details" style={{ color: "#fca5a5" }}>
                  {it.removedIngredients?.length > 0
                    ? `No ${it.removedIngredients.join(", ")}`
                    : ""}
                </div>
              </div>
              <span>${(it.price * it.qty).toFixed(2)}</span>
            </div>
          ))
        ) : (
          <p
            style={{
              color: "var(--text-medium)",
              textAlign: "center",
              marginTop: "2rem",
            }}
          >
            Your cart is empty.
          </p>
        )}
      </div>

      <div className="cart-total-section">
        <VoucherDropdown value={voucherCode} onChange={setVoucherCode} compact />

        {orderDeliveryFee > 0 && (
          <div
            style={{
              display: "flex",
              justifyContent: "space-between",
              marginBottom: "0.5rem",
              color: "var(--text-medium)",
            }}
          >
            <span>Delivery Fee</span>
            <span>${orderDeliveryFee.toFixed(2)}</span>
          </div>
        )}

        <div className="total-price-display">
          <span>Total:</span>
          <span>${finalTotal.toFixed(2)}</span>
        </div>

        <button
          type="button"
          className="place-order-button"
          disabled={
            cart.length === 0 ||
            (orderType === "Delivery" && (!orderAddress || !!orderAddressError))
          }
          onClick={() => onProceed?.()}
        >
          {isPreorder ? "Place pre-order" : "Continue"}
        </button>
      </div>
    </>
  );
}

// ThemeSwitcher
function ThemeSwitcher() {
  const { theme, setTheme } = useTheme();

  const base = {
    width: "2rem",
    height: "2rem",
    borderRadius: "50%",
    cursor: "pointer",
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    fontSize: "1.1rem",
    backgroundColor: "var(--background-light)",
    borderWidth: "2px",
    borderStyle: "solid",
    borderColor: "var(--border-color)",
    transition: "background-color 0.15s ease, border-color 0.15s ease, transform 0.15s ease",
  };

  const active = {
    ...base,
    borderColor: "var(--brand-neon-green)",
    transform: "translateY(-1px)",
  };

  return (
    <div style={{ display: "flex", gap: "0.5rem", alignItems: "center" }}>
      <button
        style={theme === "light" ? active : base}
        onClick={() => setTheme("light")}
        title="Light theme"
      >
        L
      </button>
      <button
        style={theme === "dark" ? active : base}
        onClick={() => setTheme("dark")}
        title="Dark theme"
      >
        D
      </button>
    </div>
  );
}

