// --- Safe, optional Google Maps loader (no crash if missing key) ---
function useGoogleMaps() {
  const [mapsLoaded, setMapsLoaded] = React.useState(() => !!(typeof window !== 'undefined' && window.google?.maps));

  React.useEffect(() => {
    if (typeof window === 'undefined' || typeof document === 'undefined') {
      return;
    }
    if (window.google?.maps) {
      setMapsLoaded(true);
      return;
    }
    const key = import.meta.env.VITE_GOOGLE_MAPS_API_KEY || '';
    if (!key) return;
    const scriptId = 'google-maps-script';
    const existing = document.getElementById(scriptId);
    const markLoaded = () => setMapsLoaded(true);
    if (existing) {
      if (window.google?.maps) {
        setMapsLoaded(true);
      } else {
        existing.addEventListener('load', markLoaded, { once: true });
      }
      return;
    }
    const script = document.createElement('script');
    script.id = scriptId;
    script.async = true;
    script.defer = true;
    script.src = `https://maps.googleapis.com/maps/api/js?key=${key}&libraries=places`;
    script.onload = markLoaded;
    document.head.appendChild(script);
  }, []);

  return mapsLoaded;
}

